/* Mobile Responsivness */
 @media (min-width: 750px) {
    body {
        background-color: yellow;
        display: grid;
        grid-template-columns: auto;
      grid-template-rows: min-content min-content auto min-content;  
        grid-template-areas: 
        "navigation" 
        "header" 
        "content"
        "footer";
        min-height: 100vh;
    }
    
}
.gallery,.news,.contact,.about{
    position: relative;
     /* opacity: 0.70; */
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
/* Switch off background-attachment for regular scrolling */
    background-attachment: fixed;
    
}
.gallery {
  background-image: url('24d45beb6e3fc6b4ed45a0f94aa46a10.png');
  min-height: 500px;
}

.gallery a:hover {
background: white;

}

.news {
    background-image: url('c87230cc1518d3e5577ff6dbcc803c62.jpg');
    min-height: 400px;
   
}

.contact {
    background-image: url('https://thumbs.gfycat.com/BitesizedGoodAsianpiedstarling-size_restricted.gif');
    min-height: 700px;
    
    
}

.about {
    background-image: url('5c29a969cb7aaf2c3a2bd4869f5a44c7.jpg');
    min-height: 700px;


}

.section{
    text-align: center;
    padding: 50px 80px;
    
}

.galleryText,.aboutText,.newsText,.contactText {
    position: absolute;
    top: 40%;
    width: 60%;
    text-align: center;
    color: black;
    font-size: 30px;
    letter-spacing: 2px;
    text-transform: uppercase;
    font-family: 'Press Start 2P';
    background-color: yellow;
   
}



td{
    padding: 10px;
}

th {
    font-size: 2em;
    background-color: yellow;
}

h1 {
    color: yellow;
    font-family: 'Press Start 2p';
    position: center;
}

h2 {
    color:black;
    font-family: 'Press Start 2p';
}

h3 {
    color: yellow;
    font-family: 'Press Start 2p';
}
h4{
    color: black;
    font-family: 'Press Start 2p';
    font-size: 36px;
}

p {
    color: red;
    font-family: sans-serif;
    text-align: center;
    font-size: 40px;
}

ul {
    font-family: 'Press Start 2P';
    color: yellow;
    padding: 20px;
    margin: 0 auto;
    text-decoration: none;
}

.fab {
    font-size: 40px;
    color:#ffffff;
    text-decoration: none;
}

.fab a:hover {
    color: white;
}

img {
    border-radius: 30%;
    border-style: solid;
    border-width: 5px;
    border-color: yellow;
    position: center;
    width:280px;
    height:280px;
     /* margin: 2px auto;  */
    /* margin-bottom: 20px; */

}


.navigationContainer {
    position: grid;
    background-color: black;
    background-size: relative;
    font-size: 120%;
    text-decoration: none;
    border-style: solid;
    border-color: white;
    border-radius: 20px;
}

.headerContainer {
    background-color: white;
    text-align: center;
    padding: 20px;
    margin: 10px;
    border-radius: 20px;
}

#content {
    display: block;
background-color: yellow;
text-align: center;
}



.footerContainer {
    display: grid;
    background-color: black;
    font-size: 120%;
    text-decoration: none;
    border-style: solid;
    border-color: white;
    border-radius: 20px;    
    
}

#footer {
    
    position: sticky;
    width: 100%;
    /* bottom: 0; */
    text-decoration: none;
    
}

#footerIcons {
    display: flex;
    font-size: 60px;
    text-decoration: none;
}
#navigation a:hover {
    color: black;
    font-size: 95%;
    background: yellow;
    border-radius: 10px;
}

#navigation {
    display:grid;
    position: sticky;
    top: 100;
    width: 100%;

}

#navigation>ul>li {
    list-style: none;
    padding: 20px;
    display: inline-block;
    font-size: 120%;
}

.listItem a {
    font-family: 'Press Start 2P';
    list-style: none;
    color: #ffffff;
    text-decoration: none;
    padding: 8px;

}

.navToggle {
    width: 100%;
    padding: 10p 20px;
    background: rgb(56, 56, 55);
    text-align: left;
    box-sizing: border-box;
    border-radius: 4px;
    color: #dbd5d5;
    font-size: 30px;
    display: block;
}

